mapper.select:
select * from comment 
where cm_po_num = #{cri.poNum}
order by cm_num desc
limit #{cri.pageStart}, #{cri.perPageNum}

SELECT post.*, SUM(CASE WHEN rc_state = 1 THEN 1 ELSE 0 END) AS po_up FROM post left join recommend on rc_po_num = po_num where po_bo_num = #{cri.boNum} and po_temp != 1
and (po_title like concat('%', #{cri.search},'%') or po_me_id = #{cri.search})
and po_title like concat('%', #{cri.search},'%')
and po_me_id = #{cri.search}
GROUP BY po_num ORDER BY po_num DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT count(*) FROM post where po_bo_num = #{cri.boNum}
and (po_title like concat('%', #{cri.search},'%') or po_me_id = #{cri.search})
and po_title like concat('%', #{cri.search},'%')
and po_me_id = #{cri.search}

SELECT post.*, SUM(CASE WHEN rc_state = 1 THEN 1 ELSE 0 END) AS po_up FROM post left join recommend on rc_po_num = po_num
where (po_title like concat('%', #{cri.search},'%') or po_me_id = #{cri.search})
where po_title like concat('%', #{cri.search},'%')
where po_me_id = #{cri.search}
group by po_num having po_up > 4 order by po_up desc limit 5;

mapper.update:
update post set
	po_title = #{post.po_title},
	po_content = #{post.po_content},
	po_bo_num = #{post.po_bo_num}
where
	po_num = #{post.po_num}

mapper.select:
SELECT * FROM post WHERE po_me_id = #{cri.me_id}
and po_title like concat('%', #{cri.search},'%') or po_content like concat('%', #{cri.search}, '%')
and po_title like concat('%', #{cri.search},'%')
and po_content like concat('%', #{cri.search}, '%')
ORDER BY po_num DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT * FROM comment left join post on cm_po_num = po_num WHERE cm_me_id = #{cri.me_id}
and po_title like concat('%', #{cri.search},'%') or cm_content like concat('%', #{cri.search}, '%')
and po_title like concat('%', #{cri.search},'%')
and cm_content like concat('%', #{cri.search}, '%')
ORDER BY po_num DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT report.*,po_title as re_title FROM report join post on po_num = re_po_num WHERE re_po_num = #{cri.poNum}
and re_rt_name like concat('%', #{cri.search},'%') or re_content like concat('%', #{cri.search}, '%')
and re_rt_name like concat('%', #{cri.search},'%')
and re_content like concat('%', #{cri.search}, '%')
ORDER BY re_num DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT count(*)
FROM post

	where po_title like concat('%', #{cri.search},'%') 
		or po_me_id = #{cri.search}


	where po_title like concat('%', #{cri.search},'%') 


	where po_me_id = #{cri.search}
